mapper.select:
SELECT DISTINCT A.* 
FROM BOARD A JOIN BOARD_JOIN B
ON (A.board_id = B.board_id)
WHERE A.WORKSPACE_ID = #{workspace_id} 
AND B.ID = #{id}

mapper.update:
UPDATE BOARD 
SET BOARD_TITLE = #{board_Title}
WHERE BOARD_ID = #{board_id}

mapper.delete:
DELETE BOARD_JOIN 
WHERE BOARD_ID = #{board_id} 
AND ID = #{id} 
AND WORKSPACE_ID = #{workspace_id}

mapper.select:
SELECT COUNT(*)
FROM BOARD_STAR
WHERE BOARD_ID = #{board_id} 
AND USER_ID = #{id}

mapper.update:
UPDATE BOARD 
SET BOARD_THEMA = #{board_thema}
WHERE BOARD_ID = #{board_id}

mapper.delete:
DELETE BOARD_JOIN
WHERE ID = #{id}
AND WORKSPACE_ID = #{workspace_id}

mapper.select:
SELECT WORKSPACE_ID 
FROM BOARD 
WHERE BOARD_ID = #{board_id}

mapper.delete:
DELETE BOARD_STAR
WHERE BOARD_ID = #{board_id}
AND USER_ID = #{id}
