mapper.select:
SELECT * FROM SENT 
WHERE ID = #{id}
ORDER BY SENT_DATE DESC

mapper.delete:
DELETE SENT 
WHERE ID = #{id} 
AND SENT_ID = #{sent_id}

mapper.update:
UPDATE SENT
SET SENT_STAR = #{sent_star}
WHERE SENT_ID = #{sent_id}

mapper.select:
SELECT * FROM SENT
WHERE SENT_TO LIKE '%'||#{sent_to}||'%'
AND ID = #{id}

SELECT * FROM SENT
WHERE SENT_TITLE LIKE '%'||#{sent_title}||'%'
AND ID = #{id}
