mapper.select:
SELECT * FROM CARD 
WHERE CARD_START_DATE IS NOT NULL
AND LIST_ID = #{list_id}

mapper.insert:

	SELECT MAX(CARD_ID) + 1 AS CARD_ID FROM CARD

INSERT INTO CARD (CARD_ID
				, CARD_TITLE

				, CARD_START_DATE


				, CARD_END_DATE

				, LIST_ID)
VALUES (#{card_id}
				, #{card_title}

				, #{card_start_date}


				, #{card_end_date}

				, #{list_id})
